DROP TABLE IF EXISTS `xdstar`;
CREATE TABLE `xdstar`  (
  `id` int(11) NOT NULL COMMENT '主键编号',
  `xueno` int(11) NOT NULL COMMENT '学号',
  `name` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '姓名',
